class CultivationLog{constructor(t){this.log_elem=document.getElementById(t),this.articles=[]}add_log(t,s,e,l){let p=`<div class="name">${t}</div>`,i=e.split(", "),a=this.articles.find(s=>s.name==t),o=`<div class="date">${s}</div>`;for(let n=0;n<i.length;n++)o+=`<img src="${i[n]}">`;if(void 0==l&&(l=""),o+='<p class="popup-text">'+l+"</p>",void 0==a){let c=p+o;this.articles.push({name:t,content:c})}else a.content+=o}set_logs(){this.log_elem.innerHTML=this.get_logs()}get_logs(){let t="",s="";for(let e=0;e<this.articles.length;e++){let l=this.articles[e],p="popup"+e;t+=this.get_popup_link_(p,l.name),s+=this.get_popup_content_(p,l.content)}return'<p class="indent">'+t+s+"</p>"}get_popup_link_(t,s){let e='<div class="label">';return e+=`<a href="" class="popup-open" data-modal-btn=${t}>${s}</a>`,e+="</div>"}get_popup_content_(t,s){let e=`<div class="popup-wrap" data-modal-cont=${t}>`;return e+='<div class="popup-back"></div>',e+='<div class="popup-inner"><span class="popup-close"></span><div class="popup-contents">',e+=s,e+="</div></div></div>"}}